// ‚úÖ MODIFICADO: Al iniciar la aplicaci√≥n, carga los tratamientos desde la API
document.addEventListener('DOMContentLoaded', async () => {
  // Cargar modo oscuro
  const esModoOscuro = localStorage.getItem('modoOscuro') === 'true';
  if (esModoOscuro) {
    document.body.classList.add('modo-oscuro');
    document.getElementById('btn-modo-oscuro').textContent = '‚òÄÔ∏è Modo Claro';
  }
  
  // Cargar pacientes y tratamientos desde la API
  await cargarPacientesDesdeAPI();
  await cargarTratamientosGlobalesDesdeAPI();
  
  // Inicializar odontograma
  dibujarOdontograma();
  
  // Establecer fecha actual
  const hoy = new Date().toISOString().split('T')[0];
  document.getElementById('fecha').value = hoy;
});
